import React, { Component, Suspense, useState, useEffect, useRef } from 'react';
// import React, { Component, Fragment } from 'react'
import Axios from 'axios';
import { connect } from 'react-redux'
import { userActions } from './redux/actions'

const Cari = () => {

    const [repoList, setRepoList] = useState([]);
    const [user, setUser] = useState([]);

    state = {
        datarepo: [],
        dataowner: []
     }

     pencarian=()=>{
        let cari = this.refs.usernamecari.value
        console.log(cari)

         Axios.get(`https://api.github.com/users/${cari}/repos`)
         .then((res) => {
            localStorage.setItem('ignatius',res.data[0].owner.login)
            this.props.userActions(res.data[0].owner.login)
            this.setState({ datarepo:res.data, dataowner:res.data.owner })
         }).catch((err) => {
            console.log(err)
         })
     }

     renderrepo=()=>{
        return this.state.datarepo.map((val,index) => {
            return(
                <tr key={index}>
                    <td>{val.id}</td>
                    <td>{val.name}</td>
                    <td>{val.full_name}</td>
                    <td>{val.html_url}</td>
                </tr>
            )
        })
     }

      return (
          <div>
              <div><br />
                  <input style={{width:'200px', textAlign:'center'}} ref="usernamecari" className="form-control" type="text" placeholder="Search Github Username" aria-label="Search" />
                  <button className="mb-5" type="button" onClick={this.pencarian} className="btn btn-outline-default btn-rounded waves-effect]">Search!</button>
              </div>
              {
                  this.state.datarepo.length?
                  <div>
                      <h3><strong>Repositories</strong></h3>
                      <table style={{marginLeft:'35vh'}} className="table table-bordered">
                          <thead>
                              <tr>
                                  <th scope="col">ID</th>
                                  <th scope="col">Name</th>
                                  <th scope="col">Full Name</th>
                                  <th scope="col">HTML</th>
                              </tr>
                          </thead>
                          <tbody>
                              {this.renderrepo()}
                          </tbody>
                      </table>
                  </div>
                  :
                  null
              }
          </div>
       );

}

const MapstateToprops = (state) =>{
    return{
      usernameR: state.usernameR
    }
  }

export default connect(MapstateToprops, {userActions})(Cari);
